const Keccak = artifacts.require("Keccak");

contract("Keccak", accounts => {
  let keccak;

  before(async () => {
    keccak = await Keccak.deployed();
  });

  describe("Keccak-f[1600]", async () => {
    it("first input", async () => {
      let state = Buffer.from('', 'hex');
      let ret = await keccak.keccakf.call(state);
      console.log(Buffer.from(ret).toString('hex'));
      await keccak.keccakf.sendTransaction(state);
    });
  });
});
